import { proxyNLP } from './util'
import { NlpQueryOptions } from '../index.d'
function tokenize(query: NlpQueryOptions) {
    return proxyNLP('TOKENIZE', query)
}

function ner(query: NlpQueryOptions) {
    return proxyNLP('NER', query)
}

function sentiment(query: NlpQueryOptions) {
    return proxyNLP('SENTIMENT', query)
}

function sensitive(query: NlpQueryOptions) {
    return proxyNLP('SENSITIVE', query)
}

export default { tokenize, ner, sentiment, sensitive };